
public class Ex03_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a =3;
++a;
System.out.println(a);
--a;
System.out.println(a);

int x =5;
int y = ++x;
System.out.println(x+"    "+y);

int x2 =5;
int y2 = x2++;
System.out.println(x2+ "      "+y2);

//3.증감연산자(증가/감소 연산자)
int y3 =10;
int y4 = ++y3;
System.out.println(y3+"\t"+y4);

int y5= y3++;
System.out.println(y3+"\t"+y5);

	}

}
